mapper.select:
select *
from myblog
where blog_id =#{blogid}
and blog_user_id =#{userid}
and is_deleted =0

mapper.update:
update myblog
set is_deleted =1,update_gmt = CURRENT_TIMESTAMP
where blog_id =#{blogid}
and blog_user_id =#{userid}

update myblog

    blog_title =#{blogTitle},
    blog_cover_image =#{blogCoverImage},
    blog_content =#{blogContent},
    blog_category_id =#{mycategory.categoryId}

,update_gmt = CURRENT_TIMESTAMP
where blog_id =#{blogId}
and blog_user_id =#{myuser.userId}
and is_deleted =0

mapper.select:
select blog_id,blog_title,blog_cover_image,blog_category_id,create_gmt,update_gmt
from myblog
where blog_user_id =#{userid}
and is_deleted =0
order by create_gmt desc;

select blog_id,blog_title,blog_cover_image,blog_category_id,create_gmt,update_gmt
from myblog
where blog_user_id =#{userid}
and blog_id=#{blogid}
and is_deleted =0
order by create_gmt desc;

select count(blog_user_id) from myblog
where blog_user_id =#{userid}
and is_deleted =0

select blog_id,blog_title,blog_cover_image,blog_category_id,create_gmt,update_gmt
from myblog
where blog_user_id =#{userid}
and blog_category_id =#{categoryid}
and is_deleted =0
limit #{pageNow},#{pageSize};

select *
from myblog blog
where blog_id =#{blogid}
and is_deleted =0;

select blog_id,blog_title,blog_cover_image,blog_category_id,blog_user_id,create_gmt,update_gmt
from myblog blog
where is_deleted =0

    AND blog_category_id =#{categoryid}

ORDER BY create_gmt DESC
limit 0,9;

select blog_id,blog_title,blog_cover_image,blog_category_id,blog_user_id,create_gmt,update_gmt
from myblog blog
where is_deleted =0
and blog_id =#{blogid}
ORDER BY create_gmt DESC;

select count(blog_user_id) from myblog
where is_deleted =0

    AND blog_category_id =#{categoryid}

select blog_id,blog_title,blog_cover_image,blog_category_id,blog_user_id,create_gmt,update_gmt
from myblog
where blog_user_id =#{userid}
and is_deleted =0
order by create_gmt desc
limit #{pageNow},#{pageSize};
