mapper.select:
select blog_id,blog_title,blog_cover_image,blog_category_id,blog_user_id,is_deleted,create_gmt,update_gmt from myblog where is_deleted =0
and blog_category_id =#{categoryId}
order by create_gmt desc

select *
from myblog
where blog_id = #{blogId}
  and is_deleted = 0;

select blog_id,blog_title,blog_cover_image,blog_category_id,blog_user_id,is_deleted,create_gmt,update_gmt
from myblog
where is_deleted =0

    and blog_category_id =#{categoryId}

and blog_user_id=#{userId}
order by create_gmt desc

select count(blog_user_id)
from myblog
where blog_user_id = #{userId}

    and blog_category_id =#{categoryId}

  and is_deleted = 0

select blog_id,blog_title,blog_cover_image,blog_category_id,blog_user_id,is_deleted,create_gmt,update_gmt
from myblog
where blog_user_id =#{userId}
  and blog_id=#{blogId}
  and is_deleted =0;

select *
from myblog
where blog_id = #{blogId}
  and blog_user_id =#{userId}
  and is_deleted = 0

mapper.update:
update myblog
set is_deleted =1,update_gmt = CURRENT_TIMESTAMP
where blog_id =#{blogId}
  and blog_user_id =#{userId}
